mapper.select:
SELECT cl.course_no, cl.users_no AS 'write_users_no', cl.course_name, cl.course_difficulty, cl.course_length, cl.course_time, cl.course_hit, cl.course_region, cl.course_date, cl.course_open, cl.course_introduce, clike.course_like_no, clike.login_users_no, CASE WHEN clike.login_users_no IS NOT NULL THEN 1 ELSE 0 END AS is_liked FROM course_list cl LEFT JOIN ( SELECT course_like_no, course_no, users_no AS 'login_users_no' FROM course_like WHERE users_no = #{login_users_no} ) clike ON clike.course_no = cl.course_no
where cl.users_no = #{ write_users_no }
order by cl.course_no desc


select count(*)
from course_like
where course_no= #{course_no}


select *
from course_favorites
where users_no = #{users_no} and course_no = #{course_no}

mapper.update:

          	update course_list
			set course_hit = course_hit + 1
			where course_no = #{course_no}
